import { useRef, useState } from "react";

const Searchbar = ({ heading, subheading, placeholder, setSearch }) => {
  const search = useRef();

  const searchRequests = (e) => {
    e.preventDefault();
    setSearch(search.current.value);
  };

  const Searchbar = ({ heading, subheading, placeholder, onChange }) => {
    return (
      <div id="search-bar">
        <h5 className="text-center">{heading}</h5>
        <p className="text-center">{subheading}</p>
        <form onSubmit={searchRequests}>
          <input
            className="form-control text-center w-50 mx-auto"
            type="text"
            placeholder={placeholder}
            ref={search}
          />
        </form>
      </div>
    );
  };
};

export default Searchbar;
