import { useEffect, useState } from 'react';

const RequestTable = ({ requests }) => {
  const [filteredRequests, setFilteresRequests] = useState(requests);
  const [selected, setSelected] = useState('');

  useEffect(() => {
    if (selected === 'Waiting') {
      setFilteresRequests(requests.filter(req => req.status === 'waiting'));
    } else if (selected === 'Scheduled') {
      setFilteresRequests(requests.filter(req => req.status === 'scheduled'));
    } else if (selected === 'Completed') {
      setFilteresRequests(requests.filter(req => req.status === 'completed'));
    } else if (selected === 'Canceled') {
      setFilteresRequests(requests.filter(req => req.status === 'canceled'));
    } else {
      setFilteresRequests(requests);
    }
  }, [selected, requests])

  const selectionHandler = e => {
    e.preventDefault();
    setSelected(e.target.value);
  }

  return (
    <div id="request-table" className="mt-5">
      <h3 className="float-start">Service Requests:</h3>
      <div className="float-end">
        <select className="form-select" onChange={selectionHandler}>
          <option>Filter by status</option>
          <option>Waiting</option>
          <option>Scheduled</option>
          <option>Completed</option>
          <option>Canceled</option>
        </select>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Invoice #</th>
            <th scope="col">Date Completed</th>
            <th scope="col">Type</th>
            <th scope="col">Company Name</th>
            <th scope="col">City</th>
            <th scope="col">Status</th>
            <th scope="col"></th>
          </tr>
        </thead>
        <tbody>
          {filteredRequests.map(req => (
            <tr key={req.id}>
              <td className="text-center">{req.invoiceNumber ? req.invoiceNumber : "--"}</td>
              <td className="text-center">{req.dateCompleted ? req.dateCompleted : "--"}</td>
              <td>{req.type}</td>
              <td>{req.customer.businessName}</td>
              <td>{req.customer.city}</td>
              <td>{req.status}</td>
              <td><button className="btn btn-primary">view</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}

export default RequestTable;
