import React, { useEffect, useState } from 'react';
import API from '../API/index';
import { useMutation, useQuery, useQueries, useQueryClient } from 'react-query';
import ky from 'ky';

export default function InventoryPage() {
	const {isLoading, data} = useQuery('fetchParts', API.getAllParts)

	useEffect(async ()=>{
		// const response = await API.loggedIn('bob', 'password');

		const result = await API.updatePart({
			id: 12,
			description: "just an ordinary part",
			partNumber: "32332",
			quantity: 45
		})



	}, [])

	return (
		<div className="inventory-page-wrapper container">
			<div className="row">
				<div className="col-sm">

				</div>

			</div>
			<div className="row" >
				<div className="col-sm">
					<table className="table">
						<thead>
							<tr>
								<th>Part #</th>
								<th>Description</th>
								<th style={{textAlign:"center"}}># In Inventory</th>
								{/* <th>Adjust Stock Levels</th> */}
							</tr>

						</thead>
						<tbody>
							{ isLoading ?
							<tr>Loading...</tr>
							:	
							data.map(part => {
								return(
									<tr key={part.id}> 
										<td> {part.partNumber} </td>
										<td> {part.description} </td>
										<td style={{textAlign:"center"}}> {part.quantity} </td>
										<td style={{borderBottom:"none"}}>
											<button type="button" className="btn" onClick={ () => console.log(console.log(data))}>ᐃ</button>
											<button type="button" className="btn" onClick={() => console.log()}>ᐁ</button>
										</td>
									</tr>
								)
							})}
						</tbody>

					</table>

				</div>

			</div>


		</div>
	)
}
