import { useEffect, useState } from "react";
import { useLocation, useParams } from "react-router-dom";
// import { useQuery, useQueryClient } from "react-query";
import API from "../API";
// import Container from "../components/Container/Container";
// import Col from "../components/Col/Col";
// import Row from "../components/Row/Row";
import { SideNavBar } from "../components";
// import { set } from "js-cookie";

function ViewModel() {
  const [modelResults, setModelResults] = useState([]);
  const [requests, setRequests] = useState();
  const [selectCustomer, setSelectCustomerState] = useState();
  const [customer, setCustomer] = useState();
  const location = useLocation();
  const viewModel = [];

  // console.log("DATA");
  // console.log(modelResults.data.data);

  // const queryClient = useQueryClient();

  const filterView = () => {
    requests.filter((v) => {
      v.customer.id === selectCustomer
        ? viewModel.push(v)
        : console.log(v + " not a view model");
    });
    console.log(viewModel);
  };

  const {id} = useParams()
  useEffect(() => {
    setSelectCustomerState(location.state.setCustomer);

    API.getAllRequests().then((res) => [setRequests(res)]);

    // filterView().then(
    //   setCustomer({
    //   businessName: viewModel[0].customer.businessName,
    //   contactName: viewModel[0].customer.contactName,
    //   phone: viewModel[0].customer.phone,
    //   street: viewModel[0].customer.street,
    //   city: viewModel[0].customer.city,
    //   state: viewModel[0].customer.state,
    //   zipcode: viewModel[0].customer.zipcode,
    // }));

    console.log(location.state.setCustomer);

    API.getCustomerById(location.state.setCustomer).then((res) => {
      setCustomer(res);
    });

    filterView();
  }, []);

  return (
    <>
    <SideNavBar />
    <div>Customers</div>
      <div className="container">
        <div className="row">
          <div className="col">
            <h3>{customer.businessName}</h3>
          </div>
        </div>
        <div className="row">
          <div className="col">
            <div>
              {customer.street}, {customer.city}{" "}
            </div>
            <div>
              {customer.state}, {customer.zipcode}
            </div>
          </div>
          <div className="col">
            <strong>Phone</strong> <div>{customer.phone}</div>
          </div>
          <div className="col">
            <strong>Contact Name</strong>
            <div>{customer.contactName}</div>
          </div>
        </div>
        <table className="table">
        <thead>
          <tr>
            <th scope="col">Invoice #</th>
            <th scope="col">Date Completed</th>
            <th scope="col">Type</th>
            <th scope="col">Company Name</th>
            <th scope="col">City</th>
            <th scope="col">Status</th>
            <th scope="col"></th>
          </tr>
        </thead>
        <tbody>
          {viewModel.map(req => (
            <tr key={req.id}>
              <td className="text-center">{req.invoiceNumber ? req.invoiceNumber : "--"}</td>
              <td className="text-center">{req.dateCompleted ? req.dateCompleted : "--"}</td>
              <td>{req.type}</td>
              <td>{req.customer.businessName}</td>
              <td>{req.customer.city}</td>
              <td>{req.status}</td>
              <td><button className="btn btn-primary">view</button></td>
            </tr>
          ))}
        </tbody>
      </table>
      </div>

    </>
  );
}

export default ViewModel;
