import { useMutation, useQuery, useQueries, useQueryClient } from 'react-query';
import React from 'react';
import API from '../API';

const fetchCustomers = async () => await API.getAllCustomers();
const fetchRequests = async () => await API.getAllRequests();
const fetchParts = async () => await API.getAllParts();

const HomePage = () => {
  const requests = useQuery('requests', fetchRequests);
  const customers = useQuery('customers', fetchCustomers);
  const parts = useQuery('parts', fetchParts);

  console.log("==== CUSTOMER DATA ====");
  console.log(customers.data);
  console.log("==== REQUEST DATA ====");
  console.log(requests.data);
  console.log("==== PART DATA ====");
  console.log(parts.data);

  const queryClient = useQueryClient();

  switch (customers.status || requests.status || requests.status) {
    case 'loading':
      return <h1 className="text-center my-5">Loading</h1>;
    case 'error':
      return <h4 className="text-center my-5">Database Error: {customers.error.message} || {requests.error.message} || {parts.error.message}</h4>
    default:
      return <h1 className="text-center my-5">Database Load Successful!</h1>
  }
}

export default HomePage;
