      return (
        c.businessName.includes(search) ||
        c.contactName.includes(search) ||
        c.street.includes(search) ||
        c.city.includes(search) ||
        c.state.includes(search) ||
        c.zipcode.includes(search)||
        c.phone.includes(search)
